mapper.select:
SELECT su.id, su.user_name AS userName, su.nick_name AS nickName, su.mobile, su.user_type AS userType, su.master_son_type AS masterSonType FROM sys_user su INNER JOIN user_master_son_ref msr ON su.id = msr.son_id WHERE msr.is_deleted = 0 AND su.is_deleted = 0
AND msr.master_id = #{masterUserId}
AND su.nick_name LIKE CONCAT(CONCAT('%',#{nickName,jdbcType=VARCHAR}),'%')
AND su.mobile LIKE CONCAT(CONCAT('%',#{mobile,jdbcType=VARCHAR}),'%')
AND su.user_type = #{userType}
AND su.master_son_type = #{masterSonType}

mapper.delete:
DELETE FROM user_master_son_ref
WHERE

    master_id = #{masterUserId}


  son_id = #{sonUserId}
