mapper.select:
select * from user_reviews
where 1 = 1
and
    id = #{id,jdbcType=INTEGER}

and
    user_id = #{userId,jdbcType=INTEGER}

and
    business_id = #{businessId,jdbcType=INTEGER}

and
    reviews_msg = #{reviewsMsg,jdbcType=VARCHAR}

and
    creator = #{creator,jdbcType=VARCHAR}

and
    gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}

and
    is_deleted = #{isDeleted,jdbcType=INTEGER}

and
    remark = #{remark,jdbcType=VARCHAR}

and
    fid = #{fid,jdbcType=INTEGER}

and
    pic_ids = #{picIds,jdbcType=VARCHAR}

and
    plan_id = #{planId,jdbcType=INTEGER}

and
    house_id = #{houseId,jdbcType=INTEGER}

and
    supply_demand_publisher_id = #{supplyDemandPublisherId,jdbcType=INTEGER}

and
    plan_type = #{planType,jdbcType=INTEGER}

and
    is_read = #{isRead,jdbcType=INTEGER}

and
    is_top = #{isTop,jdbcType=INTEGER}

order by id desc;

select * from user_reviews
where is_deleted = 0
and business_id = #{businessId}

    and reviews_msg like concat('%',#{content},'%')


    and gmt_create >= #{startTime}


    and gmt_create <= #{endTime}

order by is_top desc, gmt_create desc
