mapper.select:
select

from
    data_statistics.company_resource_statistics


      AND company_id = #{companyId,jdbcType=INTEGER}


      AND plan_render = #{planRender,jdbcType=VARCHAR}


      AND plan_config = #{planConfig,jdbcType=VARCHAR}


      AND share_plan_render = #{sharePlanRender,jdbcType=VARCHAR}


      AND share_plan_config = #{sharePlanConfig,jdbcType=VARCHAR}


      AND texture = #{texture,jdbcType=VARCHAR}


      AND  stick_chart_product = #{stickChartProduct,jdbcType=VARCHAR}


      AND  product_model = #{productModel,jdbcType=VARCHAR}


      AND  product_pic = #{productPic,jdbcType=VARCHAR}


      AND  shop = #{shop,jdbcType=VARCHAR}


      AND shop_bowen = #{shopBowen,jdbcType=VARCHAR}


      AND shop_engineering = #{shopEngineering,jdbcType=VARCHAR}


      AND store = #{store,jdbcType=VARCHAR}


      AND  user = #{user,jdbcType=VARCHAR}


      AND company = #{company,jdbcType=VARCHAR}


      AND date = #{date,jdbcType=INTEGER}


      AND day = #{day,jdbcType=INTEGER}


      AND  week = #{week,jdbcType=INTEGER}


      AND month = #{month,jdbcType=INTEGER}


      AND year = #{year,jdbcType=INTEGER}


      AND sys_code = #{sysCode,jdbcType=VARCHAR}


      AND creator = #{creator,jdbcType=VARCHAR}


      AND gmt_create = #{gmtCreate,jdbcType=TIMESTAMP}


      AND modifier = #{modifier,jdbcType=VARCHAR}


      gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}


      AND is_deleted = #{isDeleted,jdbcType=INTEGER}


      AND remark = #{remark,jdbcType=VARCHAR}
